<script>


// On page Load
NProgress.start();

$(document).ready(function(){
//after load
NProgress.done();
});







// dark/light mode
$(".toggle-mode").click(function(){
       let skinMode = $(this).data("update");
       $(".toggle-mode").removeClass("active");
       $(this).addClass("active");

       $("body").removeClass("dark-mode", "light-mode");
       $("body").addClass(skinMode);

        // Update DB preference 
        $.ajax({
        type: 'POST',
        url: 'function.php',
        data: {skinMode:skinMode},
        success: function(data){
        console.log(data);
        }
        });
});





// UI Main Design Style
$(".uiDesign").click(function(){
       let uiDesign = $(this).data("update");
       $(".uiDesign").removeClass("active");
       $(this).addClass("active");

       $("body").removeClass("ui-default");
       $("body").removeClass("ui-bordered",);
       $("body").addClass(uiDesign);

        // Update DB preference
        $.ajax({
        type: 'POST',
        url: 'function.php',
        data: {uiDesign:uiDesign},
        success: function(data){
        console.log(data);
        }
        });
});












// Nav Backgound
$(".headerBg").click(function(){
       let headerBg = $(this).data("update");
       $(".headerBg").removeClass("active");
       $(this).addClass("active");

       $(".nk-header").removeClass("is-light");
       $(".nk-header").removeClass("is-default",);
       $(".nk-header").removeClass("is-dark");
       $(".nk-header").removeClass("is-theme");
       $(".nk-header").addClass(headerBg);

        // Update DB preference
        $.ajax({
        type: 'POST',
        url: 'function.php',
        data: {headerBg:headerBg},
        success: function(data){
        console.log(data);
        }
        });
});





// fixed and unfix header
$(".headerOption").click(function(){
       let headerOption = $(this).data("update");
       $(".headerOption").removeClass("active");
       $(this).addClass("active");

       $(".nk-header").removeClass("nk-header-fixed", "is-regular");
       $(".nk-header").addClass(headerOption);

       $(".nk-content-inner").removeClass("nk-content-margin", "nk-content-marginR")
       if(headerOption == "nk-header-fixed"){
       $(".nk-content-inner").css({"margin-top":"75px"});
       }else{
        $(".nk-content-inner").css({"margin-top":"-30px"});
       }

        // Update DB preference
        $.ajax({
        type: 'POST',
        url: 'function.php',
        data: {headerOption:headerOption},
        success: function(data){
        console.log(data);
        }
        });
});
